import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";

export default class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      pageSize: 6,
      totalResults: 0, // Initialize totalResults state
    };
  }

  async componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = async () => {
    try {
      let apiUrl = `https://newsapi.org/v2/top-headlines?country=in&apiKey=bd206d501f8846c4882cdc38da60ea82&page=${this.state.page}&pageSize=${this.state.pageSize}`;
      let response = await fetch(apiUrl);
      let parsedData = await response.json();

      // Check if parsedData has articles and update state accordingly
      if (parsedData.articles) {
        this.setState({
          articles: parsedData.articles,
          loading: false,
          totalResults: parsedData.totalResults, // Set totalResults from API response
        });
      } else {
        this.setState({ loading: false });
      }
    } catch (error) {
      console.log("Error fetching data: ", error);
      this.setState({ loading: false });
    }
  };

  handlePrev = async () => {
    console.log("prev");
    this.setState(
      (state) => ({ page: state.page - 1 }),
      this.fetchArticles
    );
  };

  handleNext = async () => {
    console.log("next");
    this.setState(
      (state) => ({ page: state.page + 1 }),
      this.fetchArticles
    );
  };

  render() {
    const { articles, loading, page, totalResults } = this.state;
    const totalPages = Math.ceil(totalResults / 20); // Calculate total pages

    // Show loading indicator or message while fetching data
    if (loading) {
      return (
        <div className="container my-3">
          <h2>Newsify - Top headlines</h2>
          <Spinner/>
        </div>
      );
    }

    // Once data is fetched, render the articles
    return (
      <div className="container my-3">
        <h1  className="text-center">Newsify - Top headlines</h1>
        <div className="row">
          {articles.map((elem, index) => (
            <div className="col-md-4 my-3" key={index}>
              <NewsItem
                title={
                  elem.title ? elem.title.slice(0, 60) : "No title available"
                }
                description={
                  elem.description
                    ? elem.description.slice(0, 80)
                    : "No description available"
                }
                imageUrl={
                  elem.urlToImage
                    ? elem.urlToImage
                    : "https://i.kinja-img.com/image/upload/c_fill,h_675,pg_1,q_80,w_1200/763ba4ecb1ef569d8a515af6eed5290e.jpg"
                }
                newsUrl={elem.url ? elem.url : ""}
              />
            </div>
          ))}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            type="button"
            disabled={page <= 1}
            className="btn btn-dark"
            onClick={this.handlePrev}
          >
            &laquo; Previous
          </button>
          <button
            type="button"
            disabled={page >= totalPages}
            className="btn btn-dark"
            onClick={this.handleNext}
          >
            Next &raquo;
          </button>
        </div>
      </div>
    );
  }
}
