import React, { Component } from "react";
import NewsItem from "./NewsItem";

export default class News extends Component {
  constructor() {
    super();
    this.state = {
      article: [], // Initialize article as an empty array
      loading: true, // Set loading state to true initially
      page: 1,
    };
  }

  async componentDidMount() {
    let apiUrl =
      "https://newsapi.org/v2/top-headlines?country=in&apiKey=bd206d501f8846c4882cdc38da60ea82&page=1";

    try {
      let response = await fetch(apiUrl);
      let parsedData = await response.json();

      console.log(parsedData);

      // Check if parsedData has articles and update state accordingly
      if (parsedData.articles) {
        this.setState({ article: parsedData.articles, loading: false });
      } else {
        this.setState({ loading: false });
      }
    } catch (error) {
      console.log("Error fetching data: ", error);
      this.setState({ loading: false });
    }
  }

 handlePrev = () => {
    console.log("prev");
  };
  handleNext = () => {
    console.log("next");
  };
  render() {
    const { article, loading } = this.state;

    // Show loading indicator or message while fetching data
    if (loading) {
      return (
        <div className="container my-3">
          <h2>Newsify - Top headlines</h2>
          <p>Loading...</p>
        </div>
      );
    }

    // Once data is fetched, render the articles
    return (
      <div className="container my-3">
        <h2>Newsify - Top headlines</h2>
        <div className="row">
          {article.map((elem, index) => (
            <div className="col my-3" key={index}>
              <NewsItem
                title={
                  elem.title ? elem.title.slice(0, 60) : "No title available"
                }
                description={
                  elem.description
                    ? elem.description.slice(0, 80)
                    : "No description available"
                }
                imageUrl={
                  elem.urlToImage
                    ? elem.urlToImage
                    : "https://i.kinja-img.com/image/upload/c_fill,h_675,pg_1,q_80,w_1200/763ba4ecb1ef569d8a515af6eed5290e.jpg"
                }
                newsUrl={elem.url ? elem.url : ""}
              />
            </div>
          ))}
        </div>
        <div className="container d-flex justify-content-between">
          <button type="button" class="btn btn-dark" onClick={handlePrev}>
            &laquo;Previous
          </button>
          <button type="button" class="btn btn-dark" onClick={handleNext}>
            Next &raquo;
          </button>
        </div>
      </div>
    );
  }
}
