import React, { Component } from "react";
import NewsItem from "./NewsItem";

export default class News extends Component {
  article = [
    {
      status: "ok",
      totalResults: 68,
      articles: [
        {
          source: {
            id: "google-news",
            name: "Google News"
          },
          author: "AMBCrypto News",
          title: "Bitcoin whales load up as BTC stays still at $66k: What’s their strategy? - AMBCrypto News",
          description: null,
          url: "https://news.google.com/rss/articles/CBMiXGh0dHBzOi8vYW1iY3J5cHRvLmNvbS9iaXRjb2luLXdoYWxlcy1sb2FkLXVwLWFzLWJ0Yy1zdGF5cy1zdGlsbC1hdC02Nmstd2hhdHMtdGhlaXItc3RyYXRlZ3kv0gFgaHR0cHM6Ly9hbWJjcnlwdG8uY29tL2JpdGNvaW4td2hhbGVzLWxvYWQtdXAtYXMtYnRjLXN0YXlzLXN0aWxsLWF0LTY2ay13aGF0cy10aGVpci1zdHJhdGVneS9hbXAv?oc=5",
          urlToImage: null,
          publishedAt: "2024-06-17T10:02:14Z",
          content: null
        },
        {
          source: {
            id: null,
            name: "Yahoo Entertainment"
          },
          author: null,
          title: "Latest inflation data provides potential for 'even greater upside' in the stock market rally - Yahoo Finance",
          description: null,
          url: "https://finance.yahoo.com/news/latest-inflation-data-provides-potential-for-even-greater-upside-in-the-stock-market-rally-100054916.html",
          urlToImage: null,
          publishedAt: "2024-06-17T10:00:54Z",
          content: "If you click 'Accept all', we and our partners, including 238 who are part of the IAB Transparency &amp; Consent Framework, will also store and/or access information on a device (in other words, use … [+678 chars]"
        },
        {
          source: {
            id: "reuters",
            name: "Reuters"
          },
          author: "Reuters",
          title: "Shares stable, euro downtrodden as political turmoil saps market mood - Reuters",
          description: null,
          url: "https://www.reuters.com/markets/global-markets-wrapup-1-2024-06-17/",
          urlToImage: null,
          publishedAt: "2024-06-17T08:43:00Z",
          content: null
        },
        {
          source: {
            id: "usa-today",
            name: "USA Today"
          },
          author: "Daniel de Visé",
          title: "'Spaving' discounts can derail your finances - USA TODAY",
          description: "'Spaving,' a mashup of spending and saving, is a discount scheme that can play havoc with your finances.",
          url: "https://www.usatoday.com/story/money/2024/06/16/spaving-meaning-discounts-can-derail-finances/74095929007/",
          urlToImage: "https://www.usatoday.com/gcdn/authoring/authoring-images/2023/11/25/USAT/71702582007-usatsi-21962611.jpg?crop=2999,1688,x0,y197&width=2999&height=1688&format=pjpg&auto=webp",
          publishedAt: "2024-06-17T08:41:25Z",
          content: "Any time you buy two of something and get a third item for half off, or add a few dollars to an online order to secure free shipping, you are spaving.\r\nSpaving is a mashup of spending and saving: spe… [+6164 chars]"
        }
      ]
    }
  ];

  constructor() {
    super();
    this.state = {
      article: this.article[0].articles,
      loading: false
    };
  }

  async componentDidMount(){
    let apiUrl="https://newsapi.org/v2/top-headlines?country=in&apiKey=bd206d501f8846c4882cdc38da60ea82";
    let data=await fetch(apiUrl);
    let pasrsedData=await data.json();
    console.log(pasrsedData);
    this.setState({article:pasrsedData.article})
  }

  render() {
    return (
      <div className="container my-3">
        <h2>Newsify - Top headline</h2>
        <div className="row container">
          {this.state.article.map((elem, index) => {
            return (

             <div className="col my-3" key={index}>
                <NewsItem 
                  title={elem.title?elem.title.slice(0,60):"no title available"} 
                  description={elem.description?elem.description.slice(0,80):"no description available"} 
                  imageUrl={elem.urlToImage ? elem.urlToImage : "https://i.kinja-img.com/image/upload/c_fill,h_675,pg_1,q_80,w_1200/763ba4ecb1ef569d8a515af6eed5290e.jpg"} 
                  newsUrl={elem.url?elem.url:""}
                />
              </div>
              
            );
          })}
        </div>
      </div>
    );
  }
}
